            $('#image').val('');
            $(""#showLoading"").hide();
        }

        var id = 0;
        function Register() {

            $(""#showLoading"").show();

            var formdata = new FormData();

            formdata.append(""Id"", id);
            formdata.append(""title"", $('#title').val());
            formdata.append(""Link"", $('#Link').val());

            var image = $(""#image"")[0];

            for (var i in image.files) {
                formdata.append(""files"", image.files[i]);
            }

            $.ajax({
                url: ""/Banner/AddBanner"",
                type: ""Post"",
